            <form class="d-lg-flex me-auto justify-content-lg-end search-form" target="_self">
                <div class="d-flex justify-content-lg-end"><input class="border rounded form-control search-field" type="search" id="search-field" name="search" style="background: white;width: 230px;">
                    <button id="searchbtn" class="btn btn-primary d-flex d-sm-flex d-md-flex d-lg-flex d-xl-flex justify-content-center align-items-center justify-content-sm-center align-items-sm-center justify-content-md-center align-items-md-center justify-content-lg-center align-items-lg-center justify-content-xl-center align-items-xl-center" type="button" style="background: rgba(13,110,253,0);border-color: rgba(255,255,255,0);border-right-color: rgba(13,110,253,0);;width: 40px;height: 40px;margin-right: 20px;margin-left: 5px;"><i class="fas fa-search" style="margin: 10px;color: black;"></i></button></div>
            </form>
<button id="bellbtn" class="btn btn-primary d-flex d-sm-flex d-xl-flex justify-content-center align-items-center justify-content-sm-center align-items-sm-center justify-content-xl-center align-items-xl-center" type="button" style="background: rgba(13,110,253,0);border-color: rgba(255,255,255,0);border-right-color: rgba(13,110,253,0);width: 40px;height: 40px;margin-right: 2.5px;"><i class="fas fa-bell" style="margin: 5px;margin-left: 10px;margin-right: 10px;color: var(--bs-dark);"></i></button>
